import './infoslider.css'
import './APIcode.js'
import { Component } from 'react'

//document elements
const popup = document.getElementById("popup")


//js elements

let loading = true
let people = null
let slideLocat = 0
const apiurl = "https://api.sampleapis.com/rickandmorty/characters"

//mounting

function apiget(){
    fetch(apiurl)
        .then(response => response.json())
        .then(function(data){
            console.log(data[slideLocat])
            people = data
        })
        
        .catch(error => console.log("Failed To Connetct To API!!!"))
}
apiget()

function rightclick() {
    console.log("right button clicked")
}
//document js
function infopop(){
    popup.classList.remove("away")
}


//document render
function InfoSlider(){
    return(
 <div id="infoslider">
<button onclick = "leftButton()">{`<-`}</button>
<div id="wrapper">

<div id="weel">
<div class="NameCard" onclick = {infopop}>
    {                                       //render pop ups
}
<img src="images/id001.jpg" alt="Ricks image"/>
<h4 class="info">Name : <span id ="name">Rick</span></h4>
</div>


</div>
    
</div>
<div id="popup" class="away">
    <div id ="popupcard" class="InfoCard, away" >
        <div id="awaybutton" onclick = "infoAway()">X</div>

        <img src="images/id001.jpg" alt="person image"/>
        <h4 class="info">Fact A : <span>Info A</span></h4>
        <h4 class="info">Fact B : <span>Info B</span></h4>
        <h4 class="info">Fact C : <span>Info C</span></h4>
        <h4 class="info">Fact D : <span>Info D</span></h4>
        <h4 class="info">Fact E : <span>Info E</span></h4>
    </div>
</div>
<button onclick = {rightclick()}>{`->`}</button>
 </div>       
    )
}
//end of document render
export default InfoSlider
