import React, {Component} from "react";
class APIguts extends Component {
    constructor(props){
        super(props);
        this.state ={
            items:``,
            isLoaded:false,
        }
    }

componentDidMount(){
    fetch(`https://api.sampleapis.com/rickandmorty/characters`)
    .then(res => res.json())
    .then(data => {
        this.setState({
            isLoaded:true,
            items:data(),
        })
    })
}

render(){
        if(!this.state.isloaded) {
            return( <div className = "loading">Loading...</div>
            )}
        if (this.state.isloaded = true) return(
                <div className="apiguts">
                      PASSED!!!  
                </div>
            )
        

        
    }
}

export default APIguts
